import { makeAutoObservable, observable, action, computed } from "mobx";

export type DefaultEntityType = {
    id: number;
    name: string;
}

export interface DeviceEntityType extends DefaultEntityType {
    price: number;
    rating: number;
    img: string;
}

export default class DeviceStore {
    _types: DefaultEntityType[];
    _brands: DefaultEntityType[];
    _devices: DeviceEntityType[];
    _selectedType: {} | DefaultEntityType;

    constructor() {
        this._types = [
            { id: 1, name: "Холодильники" },
            { id: 2, name: "Смартфоны" }
        ];
        this._brands = [
            { id: 1, name: "Samsung" },
            { id: 2, name: "Apple" }
        ];
        this._devices = [
            { id: 1, name: "Iphone 12 Pro", price: 100000, rating: 5, img: 'data:image/jpeg;base64,'},
            { id: 2, name: "Iphone 13 Pro", price: 120000, rating: 5, img: 'data:image/jpeg;base64,'},
            { id: 3, name: "Iphone 14 Pro", price: 120000, rating: 5, img: 'data:image/jpeg;base64,'},
            { id: 4, name: "Iphone 15 Pro", price: 120000, rating: 5, img: 'data:image/jpeg;base64,'},
            { id: 5, name: "Iphone 16 Pro", price: 120000, rating: 5, img: 'data:image/jpeg;base64,'},
            { id: 6, name: "Iphone 17 Pro", price: 120000, rating: 5, img: 'data:image/jpeg;base64,'},
        ];
        this._selectedType = {};

        makeAutoObservable(this);
    }

    setTypes(types: DefaultEntityType[]) {
        this._types = types;
    }

    setBrands(brands: DefaultEntityType[]) {
        this._brands = brands;
    }

    setDevices(devices: DeviceEntityType[]) {
        this._devices = devices;
    }

    setSelectedType(type: {}) {
        this._selectedType = type;
    }

    get types() {
        return this._types;
    }

    get brands() {
        return this._brands;
    }

    get devices() {
        return this._devices;
    }

    get selectedType() {
        return this._selectedType;
    }
}